const fs = require('fs');
const envPath = 'c:\\Users\\B.Shankar\\Desktop\\Learn\\next_js\\stock.io\\.env';
let content = '';
if (fs.existsSync(envPath)) {
  content = fs.readFileSync(envPath, 'utf8');
}
const newLine = 'MONGODB_URI=mongodb+srv://B-Shankar:%40Cijnbtibo4567890%21@cluster0.smjti0z.mongodb.net/?appName=Cluster0';
if (/^MONGODB_URI=/m.test(content)) {
  content = content.replace(/^MONGODB_URI=.*$/m, newLine);
} else {
  if (content && !content.endsWith('\n')) content += '\n';
  content += newLine + '\n';
}
fs.writeFileSync(envPath, content, 'utf8');
console.log('Updated .env at', envPath);
